import React, { useState } from 'react';
import './SeatList.css'
import "../../style.css";

export default function SeatList(props) {
    let ticketNum = props.tickets;

    const [selectedSeats, setSelectedSeats] = useState([]);
    const [isSelected, setSelected] = useState(false);

    function handleSelectedState() {
        setSelected(!isSelected);

        if (ticketNum != 0) {
            if (isSelected) {
                ticketNum -= 0;
            } else {
                ticketNum += 1;
            }
        }
    }

    return (
        <React.Fragment>
            <ul className="ShowCase">
                <li>
                    <span className="seat" /> <small>Available</small>
                </li>
                <li>
                    <span className="seat selected" /> <small>Selected</small>
                </li>
                <li>
                    <span className="seat occupied" /> <small>Occupied</small>
                </li>
            </ul>

            <div className="seat-content">
                {props.items.map(seat => (
                    <div key={seat.id}>
                        {seat.available ? (
                            <button onClick={handleSelectedState} key={seat.id} className={isSelected ? "seat-item-selected" : "seat-item"}>
                                <h3>{seat.name}</h3>
                            </button>
                        ) : (
                            <button className="seat-item-occupied">
                                <h3>{seat.name}</h3>
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </React.Fragment>
    );

};
