import React, {useState, useEffect} from "react";
import { useParams, Link } from "react-router-dom";
import TrailerModal from "./TrailerModal";
import NavBar from "./Components/NavBar";
import { Movies } from "./MovieSamples";
import Footer from "./Components/Footer";
import "./style.css";

export default function MovieInfo() {


    const[title, setTitle]=useState('');
    const[genre, setGenre]=useState('');
    const[cast, setCast]=useState('');
    const[director, setDirector]=useState('');
    const[producer, setProducer]=useState('');
    const[synopsis, setSynopsis]=useState('');
    const[poster, setPoster]=useState('');
    const[trailer, setTrailer]=useState('');
    const[checked, setChecked]=useState(false);
    const[isShowing, setIsShowing]=useState(0);
    const[runtime, setRuntime]=useState(0.0);
    const[mpa, setMPA]=useState('');
    const[movies,setMovies]=useState([]);
    const movie = {title, mpa, genre, cast, director, producer, synopsis, poster, trailer, isShowing}

    useEffect(()=>{
        fetch("http://localhost:8080/movie/getAll")
        .then(res=>res.json())
        .then((result)=>{ setMovies(result);})
    },[])

    const movieTitle = useParams().movieTitle;
    const loadMovies = movies.filter(movie => movie.title === movieTitle);

    // Category String to Display to Interface
    /*var movieCat = "";
    var catLength = loadMovies.map((movie, index) => movie.category.length);
    for (let i = 0; i < catLength; i++) {
        movieCat += loadMovies.map((movie, index) => movie.category[i])
        if (i !== catLength-1) {
            movieCat += ", ";
        }
    }*/

    //Display for movies being shown
    return (
    <React.Fragment>
        <NavBar/>
        {loadMovies.map(movie => (
            <div key={movie.id} className="movie-info">
                {/* Movie Poster */}
                <div className="movie-info-row">
                    <img src={movie.poster} alt={movie.title}/>
                </div>

                <div className="movie-info-row">
                    {/* Movie Name, Rating */}
                    <div className="movie-info-text">
                        <h1>{movie.title}</h1>
                        {/*<p><b>Rated {movie.rating}</b></p>*/}
                        {/*<p><b>Category: {movieCat}</b></p>*/}
                        <p><i>{movie.synopsis}</i></p>
                    </div>
                </div>
            </div>
        ))}
        <Footer/>
    </React.Fragment>
  );
};
