import React, { useState } from 'react';
import './SeatList.css'
import "../../style.css";
import clsx from 'clsx'

export default function SeatList(props) {
    let ticketNum = props.tickets;

    const [selectedSeats, setSelectedSeats] = useState([]);
    const [isSelected, setSelected] = useState([]);

    function handleSelectedState(seat) {

    }

    return (
        <React.Fragment>
            <ul className="ShowCase">
                <li><span className="seat"/><small>Available</small></li>
                <li><span className="seat selected"/><small>Selected</small></li>
                <li><span className="seat occupied"/><small>Occupied</small></li>
            </ul>

            <div className="seat-content">
                {props.items.map(seat => (
                    <div key={seat.id}>
                        {seat.available ? (
                            <SeatSelector
                                seat={seat}
                                selectedSeats={selectedSeats}
                                onSelectedSeatsChange={selectedSeats => setSelectedSeats(selectedSeats)}
                            />
                        ) : (
                            <button className="seat-item-occupied">
                                <h3>{seat.name}</h3>
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </React.Fragment>
    );

};

function SeatSelector({ seat, selectedSeats, onSelectedSeatsChange }) {
    function handleSelectedState(seat) {
        const isSelected = selectedSeats.includes(seat)
        if (ticketNum != 0) {
            if (isSelected) {
                (selectedSeats => setSelectedSeats(selectedSeats))(
                    selectedSeats.filter(selectedSeat => selectedSeat !== seat),
                )
                ticketNum -= 0;
            } else {
                (selectedSeats => setSelectedSeats(selectedSeats))([...selectedSeats, seat])
                ticketNum += 1;
            }
        }
    }
    const isSelected = selectedSeats.includes(seat)

    return (
        <React.Fragment>


              <button tabIndex="0" key={seat.id} className={clsx('seat-item', isSelected && 'seat-item-selected')} onClick={!seat.available ? null : () => handleSelectedState(seat)}>
                <h3>{seat.name}</h3>
            </button>

        </React.Fragment>
    )
}
