import React, {useState, useEffect} from "react";
import { useParams, Link } from "react-router-dom";
import TrailerModal from "./TrailerModal";
import NavBar from "./Components/NavBar";
import { Movies } from "./MovieSamples";
import Footer from "./Components/Footer";
import "./style.css";

export default function MovieInfo() {

    const[title, setTitle]=useState('');
    const[genre, setGenre]=useState('');
    const[cast, setCast]=useState('');
    const[director, setDirector]=useState('');
    const[producer, setProducer]=useState('');
    const[synopsis, setSynopsis]=useState('');
    const[poster, setPoster]=useState('');
    const[trailer, setTrailer]=useState('');
    const[checked, setChecked]=useState(false);
    const[isShowing, setIsShowing]=useState(0);
    const[runtime, setRuntime]=useState(0.0);
    const[mpa, setMPA]=useState('');
    const[movies,setMovies]=useState([]);
    const movie = {title, mpa, genre, cast, director, producer, synopsis, poster, trailer, isShowing}

    useEffect(()=>{
        fetch("http://localhost:8080/movie/getAll")
        .then(res=>res.json())
        .then((result)=>{
            setMovies(result);
        })
    },[])

   const movieTitle = useParams().movieTitle;
   const loadMovie = movies.filter(movie => movie.title === movieTitle);

    {/*Additonal info page for movies currently being shown*/}
    return (
    <React.Fragment>
        <NavBar/>
        {loadMovie.map(movie => (
            <div key={movie.id} className="movie-info">
                <div className="movie-info-row">
                    <img src={movie.poster} alt={movie.title}/>
                </div>          
                <div className="movie-info-row">
                    {/* Movie Name, Rating */}
                    <div className="movie-info-text">
                        <h1>{movie.title}</h1>
                        <p><b>Rated {movie.mpa.mpaRating}</b></p>
                        <p><b>{movie.genre}</b></p>
                        <p><i>{movie.synopsis}</i></p>
                    </div>

                    {/* Buttons for Movie Trailer and Book Tickets */}
                    <div className="movie-info-col">
                        <div className="movie-btn">
                            {/* Button for the trailer modal view */}
                            <TrailerModal
                                trailer={movie.trailer}
                                name={movie.name}
                            />
                        </div>
                        <div className="movie-btn">
                            {/* Button to book tickets */}
                            <Link to={`/booktickets/${movie.linkName}`} className="book-tickets-linkbutton">BOOK TICKETS</Link>
                        </div>
                    </div>
                </div>
            </div>
        ))}
        <Footer/>
    </React.Fragment>
  );
};
