import React, { useState } from 'react';
import './SeatList.css'
import "../../style.css";
import clsx from 'clsx'

export default function SeatList(props) {
    let ticketNum = props.tickets;

    const [selectedSeats, setSelectedSeats] = useState([]);
    const [isSelected, setSelected] = useState([]);

    function handleSelectedState(seat) {
        setSelected(!isSelected);
        setSelectedSeats(seat.id)

        if (ticketNum != 0) {
            if (isSelected) {
                ticketNum -= 0;
            } else {
                ticketNum += 1;
            }
        }
    }

    return (
        <React.Fragment>
            <ul className="ShowCase">
                <li>
                    <span className="seat" /> <small>Available</small>
                </li>
                <li>
                    <span className="seat selected" /> <small>Selected</small>
                </li>
                <li>
                    <span className="seat occupied" /> <small>Occupied</small>
                </li>
            </ul>

            <div className="seat-content">
                {props.items.map(seat => (
                    <div key={seat.id}>
                        {seat.available ? (
                            // <div >
                            // <button onClick={() => handleSelectedState(seat)} className={(seat.id === selectedSeats) ? "seat-item-selected" : "seat-item"}>
                            //     <h3>{seat.name}</h3>
                            // </button>
                            <SeatSelector
                                seats={seat}
                                selectedSeats={selectedSeats}
                                onSelectedSeatsChange={selectedSeats => setSelectedSeats(selectedSeats)}
                            />
                        ) : (
                            <button className="seat-item-occupied">
                                <h3>{seat.name}</h3>
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </React.Fragment>
    );

};

function SeatSelector({ seats, selectedSeats, onSelectedSeatsChange }) {
    function handleSelectedState(seat) {
      const isSelected = selectedSeats.includes(seat)
      if (isSelected) {
        onSelectedSeatsChange(
          selectedSeats.filter(selectedSeat => selectedSeat !== seat),
        )
      } else {
        onSelectedSeatsChange([...selectedSeats, seat])
      }
    }

    return (
        <React.Fragment>
          {seats.map(seat => {
            const isSelected = selectedSeats.includes(seat)
            return (
              <button
                tabIndex="0"
                key={seat}
                className={clsx(
                  'seat',
                  isSelected && 'selected'
                )}
                onClick={!seat.available ? null : () => handleSelectedState(seat)}
              />
            )
          })}
        </React.Fragment>
    )
}
