import React, { useState } from 'react';
import './SeatList.css'
import "../../style.css";

export default function SeatList(props) {
    let ticketNum = props.tickets;
    var isSelected = false;

    const [selectedSeats, setSelectedSeats] = useState([]);

    function handleSelectedState() {
        isSelected = !isSelected;

        if (ticketNum !== 0) {
            if (isSelected) {
                ticketNum -= 0;
            } else {
                ticketNum += 1;
            }
        }
    }

    return (
        <React.Fragment>
            <ul className="ShowCase">
                <li>
                    <span className="seat" /> <small>N/A</small>
                </li>
                <li>
                    <span className="seat selected" /> <small>Selected</small>
                </li>
                <li>
                    <span className="seat occupied" /> <small>Occupied</small>
                </li>
            </ul>

            <div className="seat-content">
                {props.items.map(seat => (
                    <div key={seat.id}>
                        {seat.available ? (
                            <button onClick={handleSelectedState} className={isSelected ? "seat-item-selected" : "seat-item"}>
                                <h3>{seat.name}</h3>
                            </button>
                        ) : (
                            <button className="seat-item-na">
                                <h3>{seat.name}</h3>
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </React.Fragment>
    );

};


function Cinema({ movie, selectedSeats, onSelectedSeatsChange }) {
    function handleSelectedState(seat) {
      const isSelected = selectedSeats.includes(seat)
      if (isSelected) {
        onSelectedSeatsChange(
          selectedSeats.filter(selectedSeat => selectedSeat !== seat),
        )
      } else {
        onSelectedSeatsChange([...selectedSeats, seat])
      }
    }

    return (
      <div className="Cinema">
        <div className="screen" />

        <div className="seats">
          {seats.map(seat => {
            const isSelected = selectedSeats.includes(seat)
            const isOccupied = movie.occupied.includes(seat)
            return (
              <span
                tabIndex="0"
                key={seat}
                className={clsx(
                  'seat',
                  isSelected && 'selected',
                  isOccupied && 'occupied',
                )}
                onClick={isOccupied ? null : () => handleSelectedState(seat)}
                onKeyPress={
                  isOccupied
                    ? null
                    : e => {
                        if (e.key === 'Enter') {
                          handleSelectedState(seat)
                        }
                      }
                }
              />
            )
          })}
        </div>
      </div>
    )
}
