import React, {useState} from "react";
import AdminNavBar from './AdminNavBar';
import '../../style.css';

// userType: 0 = admin, 1 = user
const USERS =[
    {
        uid: 0,
        userType: 1, 
        fname: "Testing",
        lname: "User",
        email: "user@gmail.com",
        password: "password",
        phoneNum: "1234567890",
        address: {
            street: "123 UGA Rd",
            city: "Athens",
            state: "Georgia",
            zipCode: "30602"
        },
        payment: [{
            cardType: "",
            cardNum: "",
            expDate: "",
            billing: {
                street: "123 UGA Rd",
                city: "Athens",
                state: "Georgia",
                zipCode: "30602"
            }
        }]
    }
];

// provide payment information (card type, number, and expiration date, and billing address)

export default function ManageUsers() {
    // const [selectPromo, setSelectPromo] = useState("");
    // const handlePromo = (event) => {
    //     setSelectPromo(event.target.value);
    // };

    return (
        <React.Fragment>
            {/* Admin Nav Bar */}
            <AdminNavBar/>

            <div className="manage-promos">
                <div className="admin-view">
                    {/* Displays Current Promotions */}
                    <div className="movie-content">
                        {props.items.map(movie => (
                        <div key={movie.id} className="movie-item">
                            {/* Movie Title */}
                            <h3>{movie.name}</h3>
                            <p>{movie.rating}</p>
                            {/* Movie Poster which can be clicked on to view Movie Info */}
                            <div>
                            {movie.shown && (
                                <Link to={`/currentlyshowing/movieinfo/${movie.linkName}`}>
                                <img src={movie.image} alt={movie.name}/>
                                </Link>
                            )}
                            {!movie.shown && (
                                <Link to={`/comingsoon/movieinfo/${movie.linkName}`}>
                                <img src={movie.image} alt={movie.name}/>
                                </Link>
                            )}
                            </div>

                            {/* Buttons to see Movie Trailer or Book Tickets */}
                            <div>
                            {/* Button to book tickets */}
                            {movie.shown && (<Link className="book-tickets-linkbutton" to={`/booktickets/${movie.linkName}`}>BOOK TICKETS</Link>)}
                            </div>
                        </div>
                        ))}
                    </div>
                </div>
            </div>
        </React.Fragment>
    );
}
