import React from "react";
import { useState, useEffect } from 'react';
import { Link, useParams } from "react-router-dom";
import NavBar from "./NavBar";
import { TheaterLayout } from "./Seats/SeatSample";
import SeatList from "./Seats/SeatList";
import "../style.css";
import Footer from "./Footer"

export default function BookTickets() {

  const title = useState('');
  const genre =useState('');
  const cast = useState('');
  const director = useState('');
  const producer = useState('');
  const synopsis = useState('');
  const poster = useState('');
  const trailer =useState('');
  const showing = useState(false);
  const runtime = useState(0.0);
  const mpaa = useState('');
  const [movies, setMovies] = useState([]);

  useEffect(()=>{
    fetch("http://localhost:8080/movie/getAll")
    .then(res=>res.json())
    .then((result)=>{
        setMovies(result);
    })
  },[])

  const [selectedTicketType, setTicketType] = useState("");
  const [selectedDate, setSelectedDate] = useState("");


  const handleTicketType = (event) => {
    selectedTicketType(event.target.value);
  };

  const handleDate = (event) => {
    setSelectedDate(event.target.value);
  };

  const movieTitle = useParams().movieTitle;
  const loadMovie = movies.filter(movie => movie.title === movieTitle);

  return (
    <React.Fragment>
        <NavBar/>
        {loadMovie.map(movie => (
        <div key={movie.id}>
          <h1> Book Tickets for {movie.title} </h1>
          <div className="filter-movie-seating">
            <select onChange={handleTicketType} className="select-input">
              <option value="">Filter By Ticket Type</option>
              <option value="Adult">Adult - $12</option>
              <option value="Senior">Senior - $9</option>
              <option value="Child">Child - $8</option>
            </select>
          </div>
          <div className="filter-movie-seating">
            <select onChange={handleDate} className="select-input">
              <option value="">Filter By Showtime</option>
              <option value="4:30PM">4:30 PM</option>
              <option value="7:00PM">7:00 PM</option>
              <option value="10:30PM">10:30 PM</option>
            </select>
          </div>
          <div className="theater-layout">
              <h1>Seat Selection</h1> 
              <SeatList items={TheaterLayout.Seats} />
          </div>
          <div className="book-ticket-confirmation">
            <Link className="book-tickets-confirmation-link" to={`/booktickets/checkout`}><h2>CHECKOUT</h2></Link>
          </div>
        </div>
        ))}
        <Footer/>
    </React.Fragment>
  );
};
