import React, { useState } from 'react';
import './SeatList.css'
import "../../style.css";
import clsx from 'clsx'

export default function SeatList(props) {
    let ticketNum = props.tickets;
    const [selectedSeats, setSelectedSeats] = useState([]);

    return (
        <React.Fragment>
            <ul className="ShowCase">
                <li><span className="seat"/><small>Available</small></li>
                <li><span className="seat selected"/><small>Selected</small></li>
                <li><span className="seat occupied"/><small>Occupied</small></li>
            </ul>

            <div className="seat-content">
                {props.items.map(seat => (

                    <div key={seat.id}>
                        {seat.available ? (
                            <SeatSelector
                                seat={seat}
                                ticketNum={ticketNum}
                                selectedSeats={selectedSeats}
                                onSelectedSeatsChange={selectedSeats => setSelectedSeats(selectedSeats)}
                            />
                        ) : (
                            <button className="seat-item-occupied">
                                <h3>{seat.name}</h3>
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </React.Fragment>
    );

};

function SeatSelector({ seat, ticketNum, selectedSeats, onSelectedSeatsChange }) {
    var count = 0;
    const isSelected = selectedSeats.includes(seat);

    function handleSelectedState(seat) {
        const isSelected = selectedSeats.includes(seat)
        console.log(ticketNum);
        console.log(count);
        if (ticketNum !== count) {
            if (isSelected) {
                onSelectedSeatsChange(
                    selectedSeats.filter(selectedSeat => selectedSeat !== seat)
                );

                count += 1;
                console.log(ticketNum);
                console.log(count);
            } else {
                onSelectedSeatsChange([...selectedSeats, seat]);
                // ticketNum += 1;
                console.log(ticketNum);
                console.log(count);
            }
        }
    }

    return (
        <React.Fragment>
            <button tabIndex="0" 
                    key={seat.id} 
                    className={clsx('seat-item', isSelected && 'seat-item-selected')} 
                    onClick={!seat.available && (ticketNum === count) ? null : () => handleSelectedState(seat)}>
                <h3>{seat.name}</h3>
            </button>
        </React.Fragment>
    )
}
